package labassignment;

public class Q7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=1;
		int y=2;
		System.out.println("Before Swapping x="+x+",y="+y+"");
		int z=0;
		z=x;
		x=y;
		y=z;
		System.out.println("After Swapping x="+x+",y="+y+"");
		
	}

}
