
package labassignment;

public class Q8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=4;
		int y=5;
		System.out.println("Before Swapping x="+x+",y="+y+"");
		x=x+y;
		y=x-y;
		x=x-y;
		System.out.println("After Swapping x="+x+",y="+y+"");
		
		
	}

}
